mapper.select:
SELECT COUNT(*) FROM V_BOARD
WHERE BOARD_STATUS = 'Y'

	AND LES_CATEGORY = #{ct}

SELECT BOARD_NO, BOARD_TITLE, READ_COUNT, LIKE_COUNT FROM V_BOARD WHERE BOARD_STATUS = 'Y'
AND LES_CATEGORY = #{ct}
ORDER BY BOARD_NO DESC

mapper.update:
UPDATE BOARD SET
READ_COUNT = READ_COUNT + 1
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT * FROM B_ATTACHMENT
WHERE BOARD_NO = #{boardNo}
ORDER BY B_FILE_LEVEL

SELECT COUNT(*) FROM BOARD_GOOD
WHERE MEMBER_NO = #{memberNo}
AND BOARD_NO = #{boardNo}

mapper.insert:
DELETE FROM BOARD_GOOD
WHERE MEMBER_NO = #{memberNo}
AND BOARD_NO = #{boardNo}

mapper.select:
SELECT COUNT(*) FROM B_REPORT
WHERE MEM_NO = #{memberNo}
AND BOARD_NO = #{boardNo}
