mapper.select:
SELECT * FROM NOTICE_ATTACHMENT
WHERE PARENT_NOTICE_NO = #{noticeNo}
ORDER BY FILE_LEVEL

mapper.insert:
INSERT INTO ATTACHMENT SELECT SEQ_NFNO.NEXTVAL, A.* FROM (
SELECT #{item.filePath} FILE_PATH, #{item.fileName} FILE_NAME, #{item.fileLevel} FILE_LEVEL, #{item.parentNoticeNo} PARENT_NOTICE_NO FROM DUAL
)A
