mapper.select:
SELECT COUNT(*) FROM CS_GOOD
WHERE CRAFTSHOP_NO =
#{memberNo}

SELECT COUNT(*) FROM CS_GOOD
WHERE MEMBER_NO = #{memberNo}
AND CRAFTSHOP_NO = #{shopNo}

mapper.insert:
INSERT INTO CS_FILE SELECT SEQ_SFNO.NEXTVAL, A.* FROM (
SELECT #{item.filePath} FILE_PATH, #{item.fileName} FILE_NAME, #{item.shopNo} CRAFTSHOP_NO, #{item.fileLevel} FILE_LEVEL FROM DUAL
) A

mapper.select:
SELECT * FROM CS_FILE
WHERE CRAFTSHOP_NO = #{shopNo}
AND FILE_LEVEL = 0

mapper.insert:
DELETE FROM CS_GOOD
WHERE MEMBER_NO = #{memberNo}
AND CRAFTSHOP_NO = #{shopNo}

mapper.select:
SELECT * FROM CS_FILE
WHERE CRAFTSHOP_NO = #{shopNo}
ORDER BY FILE_LEVEL

mapper.update:
UPDATE CS_FILE SET
FILE_PATH = #{filePath},
FILE_NAME = #{fileName}
WHERE FILE_NO = #{fileNo}

mapper.delete:
DELETE FROM CS_FILE
WHERE FILE_NO IN

	#{fileNo}

mapper.update:
UPDATE CRAFTSHOP
SET SHOP_REGI_FL = 'Y'
WHERE CRAFTSHOP_NO = #{shopNo}
